import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable'
import {currentDateToText, loadRemoteFontBase64} from './utils'
import axios from 'axios'

// Formato 5 - Solicitud de Registro
/**
 * This format is created from scratch, using jsPDF library due to the complexity of the format.
 * @param id - The data to be included in the PDF.
 */
export async function registrationRequestPdf(id: number) {
    // Get the registration data from the API using id at the endpoint /api/format?entity_id={id} with axios
    const registration = await axios.get(import.meta.env.VITE_SIRC_API_URI + `format?entity_id=${id}`);
    const registrationData = registration.data.data;
    // console.log(registrationData);
    let municipalities = registrationData.municipalities.map(municipality => municipality.name).join(', ')
    console.log("Municipalities ", municipalities)


    // URL de la fuente remota
    const fontUrl = 'https://s3.us-east-1.amazonaws.com/static-test.appsiepcdurango.mx/sirc_template_forms/ARIALN.TTF';
    const fontArialNarrowBoldUrl = 'https://s3.us-east-1.amazonaws.com/static-test.appsiepcdurango.mx/sirc_template_forms/ARIALNB.TTF';
    const fontNames = ['ARIALN', 'ARIALNB'];

    const logoBase64 = 'data:image/png;base64,';
    // Cargar la fuente remota
    const fontBytes = await loadRemoteFontBase64(fontUrl);

    const arialNarrowBoldBytes = await loadRemoteFontBase64(fontArialNarrowBoldUrl);
    // Landscape export, 2×4 inches

    let doc = new jsPDF({
        unit: 'cm',
        format: 'letter'
    }).setFontSize(12).setProperties({title: 'Formato 5 - Solicitud de Registro', author: 'SIRC IEPC Durango'})
    // Agregar logo

    doc.addImage(logoBase64, 'PNG', 3, 1.5, 4.18, 1.83)
    // Agregar la fuente al documento PDF
    doc.addFileToVFS(`${fontNames[0]}.ttf`, fontBytes);
    doc.addFileToVFS(`${fontNames[1]}.ttf`, arialNarrowBoldBytes);
    doc.addFont(`${fontNames[0]}.ttf`, 'ARIALN-Normal', 'normal');

    doc.addFont(`${fontNames[1]}.ttf`, 'ARIALNB', 'normal');
    const margins = 3
    const pageWidth = doc.internal.pageSize.getWidth()
    const contentWidth = pageWidth - (2 * margins)
    // Fecha
    doc.setFont('ARIALN-Normal', 'normal').text(currentDateToText(), 10.7, 4.5, {align: 'left', maxWidth: contentWidth})
    // Encabezado
    const header = `Registro de Candidaturas\nProceso Electoral Local 2024 - 2025\nFORMATO 5. Solicitud de Registro`
    doc.setFont('ARIALN-Normal', 'normal').text(header, 18.5, 2, {align: 'right'})
    // Datos del receptor
    doc.setFont('ARIALNB', 'normal').text('M.D. Roberto Herrera Hernández\nConsejero Presidente del\nInstituto Electoral y de Participación Ciudadana\ndel Estado de Durango\nPresente.', 3, 5.5)
    // Cuerpo
    // As subscribers can be 1 or two, we need to adjust the text accordingly

    let subscribers = registrationData.subscribed.length > 1 ? `Los que suscriben ${registrationData.subscribed[0].name} y ${registrationData.subscribed[1].name} en su carácter de ${registrationData.subscribed[0].ownership} y ${registrationData.subscribed[1].ownership}` : `El que suscribe ${registrationData.subscribed[0].name} en su carácter de ${registrationData.subscribed[0].ownership}`
    let pharagraph = `${subscribers} del ${registrationData.entity}, personalidad que se encuentra debidamente acreditada y reconocida ante este Organismo Público Local, con fundamento en lo dispuesto por los artículos 35, fracción II, 41  fracción I y 115, fracción I de la Constitución Política de los Estados Unidos Mexicanos; 3, numeral 1 de la Ley General de Partidos Políticos; 147 y 148 de la Constitución Política del Estado Libre y Soberano de Durango; 10, numeral 1, 19, 20 numeral 1, fracción III; 25, 27 numeral 1, fracción I, 184 numeral 1; 186 numeral 1, fracción II, 187 y demás relativos y aplicables de la Ley de Instituciones y Procedimientos Electorales para el Estado de Durango; 36, 37, 38, 40, 41 y 43 de los Lineamientos para el Registro de Candidaturas para la Renovación de los Ayuntamientos del Estado de Durango, para el Proceso Electoral Local 2024 – 2025; que presenta el ${registrationData.entity}, manifestando expresamente que dichas candidaturas fueron seleccionadas de conformidad con las normas estatutarias que rigen a nuestro partido.`
    pharagraph += `\n\nDe conformidad con lo establecido en el artículo 187 de la Ley de Instituciones y Procedimientos Electorales para el Estado de Durango y 36 de los Lineamientos adjunto a la presente solicitud se ${registrationData.total_registrations > 1 ? ('entregan ' + registrationData.total_registrations + ' Formatos') : ('entrega ' + registrationData.total_registrations + ' Formato')} de Registro de Candidatura a Presidencia Municipal, Sindicatura y Regidurías, correspondientes a los municipios de ${municipalities}`
    pharagraph += `, en donde se precisan los siguientes datos de cada una de las candidaturas`
    const listPositionsY = [18, 18.5, 19, 19.5, 20, 20.5, 21, 22, 23.5]
    const listBullets = ['I.', 'II.', 'III.', 'IV.', 'V.', 'VI.', 'VII.', 'VIII.', 'IX.']
    const list = [
        'Nombre completo.',
        'Lugar y fecha de nacimiento.',
        'Domicilio y tiempo de residencia en el mismo.',
        'Ocupación',
        'Clave de la credencial para votar',
        'CURP',
        'Cargo para el que se postula (Presidencia Municipal, Sindicatura o Regiduría, en el carácter de propietaria o suplente).',
        'Las candidaturas que buscan la elección consecutiva en sus cargos, acompañan una carta especificando los periodos para los que han sido electos en ese cargo y la manifestación de estar cumpliendo los límites establecidos por la Constitución en materia de reelección.',
        'En el caso de la ciudadanía perteneciente a algún grupo o sector social vulnerable, además de la documentación comprobatoria que acredite su pertenencia al grupo o sector social vulnerable que representan, me permito informar que éstas se encuentran contenidas conforme la siguiente tabla:'
    ]

    doc.setFont('ARIALN-Normal', 'normal').text(pharagraph, 3, 8.25, {align: 'justify', maxWidth: contentWidth})

    for (let i = 0; i < list.length; i++) {
        doc.setFont('ARIALNB', 'normal').text(listBullets[i], 3.5, listPositionsY[i], {align: "right"})
        doc.setFont('ARIALN-Normal', 'normal').text(list[i], 4, listPositionsY[i], {align: 'justify', maxWidth: (contentWidth - 1)})
    }
    // Numero de página
    doc.setFontSize(12).setFont('ARIALNB', 'normal').text('1 | 2', 17.8, 26)

    doc.addPage()

    // Agregar logo
    doc.addImage(logoBase64, 'PNG', 3, 1.5, 4.18, 1.83)
    // Agregar la fuente al documento PDF
    doc.addFont(`${fontNames[0]}.ttf`, 'ARIALN-Normal', 'normal');
    doc.addFont(`${fontNames[1]}.ttf`, 'ARIALNB', 'normal');
    // Encaezado
    doc.setFont('ARIALN-Normal', 'normal').text(header, 18.5, 2, {align: 'right'})
    // Cuerpo
    // Tabla de candidaturas con las columnas Nombre, Cargo, Calidad (Propietario, Suplente), Grupo vulnerable al que representa.
    // Usando el paquete https://github.com/simonbengtsson/jsPDF-AutoTable
    autoTable(doc, {
        body: registrationData.compensatories.map((row, index) => ({
            index: index + 1,
            ...row,
            name: row.name.split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()).join(' ') // 😖
        })),
        columns: [
            {header: 'No.', dataKey: 'index'},
            {header: 'Nombre', dataKey: 'name'},
            {header: 'Cargo', dataKey: 'postulation'},
            {header: 'Calidad', dataKey: 'position'},
            {header: 'Grupo vulnerable', dataKey: 'compensatory'}
        ],
        headStyles: {
            fillColor: [0, 0, 0],
            textColor: [255, 255, 255]
        },
        startY: 4.5,
        styles: {
            font: 'ARIALN-Normal',
            fontStyle: 'normal',
            fontSize: 12,
            lineWidth: 0.01,
            lineColor: [0, 0, 0]
        }
    })
    // Add text dynamically positioned at Y axis  after the table
    // En Y = 4.5 (Punto donde empieza la tabla) + número de candidaturas + 1 (margen de error)
    let dinamicYafterTable = 4.5 + registrationData.compensatories.length + 1;
    doc.setFont('ARIALN-Normal', 'normal').text(`A la presente solicitud se anexa también la documentación requerida de conformidad a la legislación aplicable para efectos de acreditar los requisitos de elegibilidad de cada una de las candidaturas:`, 3, dinamicYafterTable, {align: 'justify', maxWidth: contentWidth})

    const listB = [
        'Declaración de aceptación de la candidatura. ',
        'Sobrenombre o apodo de las candidaturas a Presidencia Municipal propietarias.',
        'Copia del Acta de nacimiento.',
        'Copia de la credencial para votar con fotografía (ambos lados).',
        'Fotografía de la candidatura de Presidencia Municipal propietaria.',
        'Constancia de residencia expedida por la autoridad municipal.',
        'Carta bajo protesta de decir verdad que cumple con los requisitos establecidos en el artículo 69 de la Constitución Política del Estado Libre y Soberano de Durango.',
        'Formato 8 de 8 contra la violencia.',
        'Constancia de registro en tiempo y forma de la plataforma electoral que sostendrán las candidaturas para el Proceso Electoral Local 2024 – 2025.',
        'Carta donde se especifican periodos por los que han sido electos, en caso de reelección.',
        'Solicitud de registro en el Sistema Nacional de Registro de Precandidatos y Candidatos del Instituto Nacional Electoral.',
        'Acuse de recibo de entrega de informe de gastos de precampaña o no precampaña, según corresponda, ante el Instituto Nacional Electoral.'
    ]
    const listBBullets = ['a)', 'b)', 'c)', 'd)', 'e)', 'f)', 'g)', 'h)', 'i)', 'j)', 'k)', 'l)']
    const listBPositionsY = [dinamicYafterTable + 0.5 + 1, dinamicYafterTable + 0.5 + 1.5, dinamicYafterTable + 0.5 + 2, dinamicYafterTable + 0.5 + 2.5, dinamicYafterTable + 0.5 + 3, dinamicYafterTable + 0.5 + 3.5, dinamicYafterTable + 0.5 + 4, dinamicYafterTable + 0.5 + 5, dinamicYafterTable + 0.5 + 5.5, dinamicYafterTable + 0.5 + 6.5, dinamicYafterTable + 0.5 + 7, dinamicYafterTable + 0.5 + 8]

    for (let i = 0; i < listB.length; i++) {
        doc.setFont('ARIALNB', 'normal').text(listBBullets[i], 3.5, listBPositionsY[i], {align: "right"})
        doc.setFont('ARIALN-Normal', 'normal').text(listB[i], 4, listBPositionsY[i], {align: 'justify', maxWidth: (contentWidth - 1)})
    }

    doc.text(`Sin otro particular, agradecemos el trámite que se realice a la presente solicitud.`, 3, listBPositionsY[listBPositionsY.length - 1] + 1.5, {align: 'justify', maxWidth: contentWidth})

    // Firmas
    // Firma de los suscriptores, agregar hasta 2 firmas con linea de firma y debajo el nombre
    const signaturesY = listBPositionsY[listBPositionsY.length - 1] + 3
    const signatureLineY = signaturesY + 1
    const signatureNameY = signatureLineY + 0.5
    const signatureLineX = 10 / 2
    const signatureNameX = 10 / 2
    const signatureLineLength = 5
    const signatureLineThickness = 0.01
    const signatureLineColor = [0, 0, 0]
    const signatureLineOffset = 0.5
    const pageWidthX = doc.internal.pageSize.getWidth();
    const textWidthX = doc.getTextWidth('ATENTAMENTE');
    const centerX = (pageWidthX - textWidthX) / 2;
    doc.setFont('ARIALNB', 'normal').text('ATENTAMENTE', centerX, 19.3);

    // Firma 1
    doc.setLineWidth(signatureLineThickness)
    doc.setDrawColor(...signatureLineColor)
    doc.line(signatureLineX - signatureLineLength / 2, signatureLineY, signatureLineX + signatureLineLength / 2, signatureLineY)
    doc.setFont('ARIALN-Normal', 'normal').text(registrationData.subscribed[0].name, signatureNameX, signatureNameY, {align: 'center'})
    // Firma 2
    if (registrationData.subscribed.length > 1) {
        doc.setLineWidth(signatureLineThickness)
        doc.setDrawColor(...signatureLineColor)
        doc.line(signatureLineX + 10 - signatureLineLength / 2, signatureLineY, signatureLineX + 10 + signatureLineLength / 2, signatureLineY)
        doc.setFont('ARIALN-Normal', 'normal').text(registrationData.subscribed[1].name, signatureNameX + 10, signatureNameY, {align: 'center'})
    }

    // Get page number dinamically
    const totalPages = doc.internal.getNumberOfPages()
    // Get current page number
    const currentPage = doc.internal.getCurrentPageInfo().pageNumber
    // Numero de página
    doc.setFontSize(12).setFont('ARIALNB', 'normal').text(currentPage + ' | ' + totalPages, 17.8, 26)

    // Aviso de privacidad
    const privacyNotice = `\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\tEl Instituto Electoral y de Participación Ciudadana del Estado de Durango, con domicilio en la calle Litio s/n., de la Colonia Cd. Industrial, de la ciudad de Victoria de Durango, Dgo., con código postal 34208, es el responsable del tratamiento de los datos personales para el registro de candidaturas ante el instituto, los cuales serán protegidos conforme a lo dispuesto por la Ley de Protección de Datos Personales en Posesión de Sujetos Obligados en el Estado de Durango, y demás normatividad que resulte aplicable. Se informa que serán transferidos los datos personales al Instituto Nacional Electoral y órganos jurisdiccionales locales y federales. No se realizarán transferencias adicionales, salvo los casos en los que sean necesarios para atender requerimientos de información de una autoridad competente, que estén debidamente fundados y motivados. Usted podrá consultar el aviso de privacidad integral en el portal del Instituto Electoral y de Participación Ciudadana del Estado de Durango.`;
    doc.setFontSize(7).setFont('ARIALN-Normal', 'normal').text(privacyNotice, 3, 23.5, {align: 'justify', maxWidth: contentWidth})
    doc.setFont('ARIALNB', 'normal').text('AVISO DE PRIVACIDAD:', 3, 23.5)
    doc.textWithLink('https://www.iepcdurango.mx/IEPC_DURANGO/informes/avisos_de_privacidad', 3, 25.2, {url: 'https://www.iepcdurango.mx/IEPC_DURANGO/informes/avisos_de_privacidad '})

    doc.save('two-by-four.pdf');
}